import React, { useState, Fragment } from 'react';
import '../form/Form.css';

const Form = () => {

    const [data, setData] = useState({
        num1: 0,
        num2: 0
    })

    const handleInputChange = (event) => {
        setData({
            ...data,
            [event.target.name]: event.target.value
        })
    };

    const sendData = (event) => {
        event.preventDefault();
        console.log(data.num1 + ' ' + data.num2)
    }

    return (
        <Fragment>
            <form id="form-body" className="row" onSubmit={sendData}>
                <div className="col-md-12 p-3">
                <label>
                    Ingrese el segundo operando
                    <input
                        placeholder="Ingrese un numero"
                        className="form-control"
                        type="number"
                        name="num1"
                        onChange={handleInputChange}
                    />
                    </label>
                </div>

                <div className="col-md-12 p-3">
                <label>
                    Ingrese el segundo operando
                    <input
                        placeholder="Ingrese un numero"
                        className="form-control"
                        type="number"
                        name="num2"
                        onChange={handleInputChange}
                    />
                    </label>
                </div>

                <div className="col-md-12 p-3">
                    <label>
                        Seleccione el operador:
                        <select name="op" value={data.value} onChange={handleInputChange}>
                            <option value="+">+</option>
                            <option value="-">-</option>
                            <option value="*">*</option>
                            <option value=":">/</option>
                        </select>
                    </label>


                </div>

                <div className="col-md-12 p-3">
                    <button className="btn btn-outline-light" type="submit">Enviar</button>
                </div>

            </form>
            <h3>{data.num1} - {data.num2}</h3>
        </Fragment>

    );
}

export default Form;
