                    <input
                        placeholder="Ingrese un numero"
                        className="form-control"
                        type="number"
                        name="num2"
                        onChange={handleInputChange}
                    />
                </div>

                <div className="col-md-3">
                    <button className="btn btn-outline-light" type="submit">Enviar</button>
                </div>

            </form>
            <h3>{data.num1} - {data.num2}</h3>
        </Fragment>

    );
}

export default Form;
