import React, { useState, Fragment, useEffect } from 'react';
import '../form/Form.css';

const Form = () => {



    const handleInputChange = (event) => {
        setData({
            ...data,
            [event.target.name]: event.target.value
        })
    };

    const sendData = async (event) => {
        const [data, setData] = useState()
        event.preventDefault();
        try {

            let config = {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            }

            let res = await fetch(`http://localhost:8000/${data.num1}/${data.op}/${data.num2}`, config)
            let json = await res.json()
            setData(json)

            console.log({})

        } catch (error) {
            console.log(error)
        }
    }


    useEffect(() => {
        sendData()
    }, [])

    return (
        <Fragment>
            <form id="form-body" className="row" onSubmit={sendData}>
                <div className="col-md-12 p-3">
                    <label>
                        Ingrese el segundo operando
                    <input
                            placeholder="Ingrese un numero"
                            className="form-control"
                            type="number"
                            name="num1"
                            onChange={handleInputChange}
                        />
                    </label>
                </div>

                <div className="col-md-12 p-3">
                    <label>
                        Ingrese el segundo operando
                    <input
                            placeholder="Ingrese un numero"
                            className="form-control"
                            type="number"
                            name="num2"
                            onChange={handleInputChange}
                        />
                    </label>
                </div>

                <div className="col-md-12 p-3">
                    <label>
                        Seleccione el operador:
                        <select
                            className="form-control "
                            name="op"
                            /*                       value={data.value}  */
                            onChange={handleInputChange}
                        >
                            <option value="+">+</option>
                            <option value="-">-</option>
                            <option value="*">*</option>
                            <option value=":">/</option>
                        </select>
                    </label>


                </div>

                <div className="col-md-12 p-3">
                    <button className="btn btn-outline-light" type="submit">Enviar</button>
                </div>

            </form>
            <ul>
                {!data ? 'Cargando...' : [data].map((dat, index) => {
                    return <li> {data.resultado} </li>
                })}
            </ul>
        </Fragment>

    );
}

export default Form;
